import React, { useState, useRef } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMicrophone } from '@fortawesome/free-solid-svg-icons';

const RecordButton = ({ setText, setHasResults, language }) => {
    const [isRecording, setIsRecording] = useState(false);
    const recognition = useRef(null); // Using useRef to maintain a persistent reference

    const startRecording = () => {
        if (isRecording) {
            recognition.current && recognition.current.stop(); 
            setIsRecording(false);
            return;
        }

        recognition.current = new window.webkitSpeechRecognition();
        recognition.current.lang = language; 

        recognition.current.onstart = () => {
            setIsRecording(true);
        };

        recognition.current.onresult = async (event) => {
            const originalText = event.results[0][0].transcript;

            const response = await axios.post('http://localhost:5000/translate', {
                text: originalText,
                targetLanguage: language
            });

            const translatedText = response.data.translatedText;
            setText(translatedText);
            setHasResults(true);

            recognition.current && recognition.current.stop(); // Stop the recognition once we have a result
        };

        recognition.current.onerror = () => {
            setHasResults(false);
        };

        recognition.current.onend = () => {
            setIsRecording(false); // Reset recording state after stopping recognition
        };

        recognition.current.start();
    };

    return (
        <button type="button" className="button" onClick={startRecording}>
            <FontAwesomeIcon icon={faMicrophone} className={`microphone-icon ${isRecording ? 'blinking' : ''}`} />
            Record
        </button>
    );
};

export default RecordButton;
