import React, { useState, useEffect } from 'react';
import axios from 'axios';

function RecordingsList() {
    const [recordings, setRecordings] = useState([]);

    useEffect(() => {
        fetchRecordings();
    }, []);

    const fetchRecordings = async () => {
        try {
            const response = await axios.get('http://localhost:5000/get-blobs');
            setRecordings(response.data);
            console.log(response.data);
        } catch (error) {
            console.error("Error fetching recordings:", error);
        }
    };

    const deleteBlob = async (blobName) => {
        try {
            await axios.delete(`http://localhost:5000/delete-blob/${blobName}`);
            // Refresh the list after deleting
            fetchRecordings();
        } catch (error) {
            console.error("Error deleting blob:", error);
        }
    }

    return (
        <div className="recordings-list">
            <h2>Recordings</h2>
            {recordings.map((record, index) => (
                <div key={index}>
                    <p>{record.name}</p>
                    <p>Translation: {record.metadata?.translation || 'No translation available'}</p>
                    <button onClick={() => deleteBlob(record.name)}>Delete</button>
                </div>
            ))}
        </div>
    );
}

export default RecordingsList;
