import React, { useState } from 'react';
import axios from 'axios';
import RecordButton from './RecordButton';
import Dropdown from './Dropdown';
import './App.css';

function App() {
  const [text, setText] = useState('');
  const [language, setLanguage] = useState('en-US');
  const [hasResults, setHasResults] = useState(false);

  const onLanguageChange = (selectedLanguage) => {
      setLanguage(selectedLanguage);
  };

  const onSubmit = (e) => {
      e.preventDefault();
      // You can now send data to Azure SQL here or any other logic you had in mind
  };




    const getTranslationFromBackend = async (text, targetLang) => {
        const response = await axios.post('http://localhost:5000/translate', { text, targetLang });
        return response.data.translatedText;
    };

    const saveDataToAzure = async (data) => {
        return await axios.post('http://localhost:5000/save-data', data);
    };

    return (
      <div className="App">
          <div className="card">
              <div className="title">Speech to Text Demo</div>
              <form onSubmit={onSubmit}>
                  <label className="label">Choose Language:</label>
                  <Dropdown onLanguageChange={onLanguageChange} />
                  <RecordButton setText={setText} setHasResults={setHasResults} language={language} />
                  {hasResults && <div>Transcribed text in English: {text}</div>}
                  <button className="button" type="submit">Submit</button>
              </form>
              {error && <div className="error">{error}</div>}
          </div>
      </div>
  );
}


export default App;
