import axios from 'axios';
import RecordButton from './RecordButton';
import Dropdown from './Dropdown';
import './App.css';

function App() {
  const [text, setText] = useState('');
  const [language, setLanguage] = useState('en-US');
  const [hasResults, setHasResults] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null); // Add this line to manage audio blob

  const onLanguageChange = (selectedLanguage) => {
      setLanguage(selectedLanguage);
  };

  const onSubmit = (e) => {
      e.preventDefault();
      // You can now send data to Azure SQL here or any other logic you had in mind
  };

    return (
      <div className="App">
          <div className="card">
              <div className="title">Speech to Text Demo</div>
              <form onSubmit={onSubmit}>
                  <label className="label">Choose Language:</label>
                  <Dropdown onLanguageChange={onLanguageChange} />
                  <RecordButton 
                      setText={setText} 
                      setHasResults={setHasResults} 
                      language={language} 
                      setAudioBlob={setAudioBlob} />  {/* Pass setAudioBlob as a prop */}
                  {hasResults && <div>Transcribed text in English: {text}</div>}
                  {/* You can also add a playback control for the audio blob here if you want */}
                  {/*  <button className="button" type="submit">Save</button> */}
              </form>
          </div>
      </div>
  );
}

export default App;
